def do(apps, editor):
    Category = apps.get_model('osobnyak', 'Category')
    Product = apps.get_model('osobnyak', 'Product')
    ProductImage = apps.get_model('osobnyak', 'ProductImage')

    cats = {}
    for i, (slug, name) in enumerate(categories):
        cats[slug] = Category.objects.create(
            title=name,
            slug=slug,
            ordering=i,
        )

    for i, item in enumerate(items):
        product = Product.objects.create(
            title=item['title'],
            category=cats[item['category']],
            price=item['price'],
            description=item['description'],
            ordering=i,
        )
        for i, image_path in enumerate(item['images']):
            ProductImage.objects.create(
                product=product,
                image=image_path,
                ordering=i,
            )


class Migration(migrations.Migration):
    dependencies = [
        ('osobnyak', '0001_initial'),
    ]

    operations = [
        migrations.RunPython(do, migrations.RunPython.noop)
    ]
